  final String name;
  final String emoji;

  const Country({
    required this.name,
    required this.emoji,
  });

  @override
  String toString() {
    return "Country(name: '$name', emoji: '$emoji')";
  }

  static Country fromJson(Map<String,dynamic> json){
    return Country(name: json["name"], emoji: json["emoji"]);
  }

}
